import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Link from '@material-ui/core/Link'
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid'
import InfoIcon from '@material-ui/icons/Info';
import PageHeader from "../../components/PageHeader";
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import CircularProgress from '@material-ui/core/CircularProgress';
import Fade from '@material-ui/core/Fade';
import axios from "axios";
import defaultImg from '../../assets/img/defaultImg.jpeg'
import { CSVDownloader } from 'react-papaparse'
import DownloadIcon from '@mui/icons-material/Download';
import Tooltip from '@mui/material/Tooltip';

const useStyles = makeStyles(theme => ({
    cardContainer: {
        width: 800,
        justifyContent: "center",
        alignItems: "center"
    },
    media: {
        height: 300,
    },
    table: {
        width: '90%',
        margin: '50px 0 0 0'
    },
    thead: {
        '& > *': {
            fontSize: 20,
            background: '#8ade8f',
            color: '#FFFFFF'
        }
    },
    row: {
        '& > *': {
            fontSize: 18
        }
    },
    buttonheader: {
        display: 'flex'

    },
    programholder: {
        height: 40,
        textAlign: 'center',
        width: '90%'
    },
    button: {
        background: '#4287f5',
        color: '#FFFFFF'
    },
    pageContent: {
        width: '90%',
        margin: '50px 0 0 0',
        padding: theme.spacing(3),
    },
    center: {
        display: 'flex',
        textAlign: 'center'
    },
}));

const initialValue = {
    name: '',
    description: '',
    objetivesProgram: '',
    definitionProgram: '',
    image: '',
    projects: [],
}

const initialProyectValues = {
    name: '',
    description: '',
    objetives: '',
    justification: '',
    country: '',
    department: '',
    district: '',
    definition: '',
    isTimeSeries: true,
    image: '',
    programs: [],
    factors: []
}



export default function ProgramServices() {
    const [program, setProgram] = useState(initialValue);
    const { name, description, objetivesProgram, definitionProgram, image } = program;
    const [toExport, setExport] = useState([]);

    const [open, setOpen] = React.useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = React.useState(true);

    const [projects, setProjects] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);


    const classes = useStyles();
    const { id } = useParams();

    function wrapValues(projects) {
        setProjects(projects);
        setLoading(false);
    }

    useEffect(async () => {
        let unmounted = false;
        await getProgram();
        return () => { unmounted = true; };
    }, []);

    const config = {
        headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        },
    };

    const getProgram = async () => {
        try {
            setLoading(true);
            let response = await axios.get(`${process.env.REACT_APP_API_URL}/api/private/program/${id}`, config);
            setProgram(response.data.program);
            let data = response.data.program;
            setExport([
                {
                    id: data.id,
                    name: data.name,
                    description: data.description,
                    objetivesProgram: data.objetivesProgram,
                    definitionProgram: data.definitionProgram,
                }])

            setLoading(false);
        } catch (error) {
            setTimeout(() => {
                setOpen(false);
                setTimeout(() => {
                    setError("");
                }, 2000);

            }, 5000);
            setOpen(true);
            setLoading(false);
            return setError("Authentication failed!");
        }
    }

    return (

        <div className={classes.root}>
            <div className={classes.programholder} hidden={!loading}>
                <Fade
                    in={loading}
                    style={{
                        transitionDelay: '0m',
                    }}
                    unmountOnExit
                >
                    <CircularProgress />
                </Fade>
                <br />
            </div>

            <PageHeader
                title="Información detallada sobre un laboratorio"
                icon={<InfoIcon fontSize="large"
                />}
            />
            <br />
            <Grid
                container
                direction="row"
                justifyContent="center"
                alignItems="center"
                style={{ width: "90%" }}
            >
                <Card className={classes.cardContainer}>

                    <CardMedia
                        className={classes.media}
                        image={image ? image : defaultImg}
                        title="Contemplative Reptile"
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                            {name ? name : ''}
                        </Typography>
                        <Typography variant="subtitle1" color="textSecondary" component="p">
                            {description ? description : ''}
                        </Typography>
                    </CardContent>

                    <CardActions>
                        <Typography variant="subtitle1" color="primary" component="p">
                            <span>Teléfono:</span>
                            {objetivesProgram ? objetivesProgram : ''}
                        </Typography>
                    </CardActions>
                    <CardActions>
                        <Typography variant="subtitle1" color="primary" component="p">
                            <span>Correo Electrónico:</span>
                            {definitionProgram ? definitionProgram : ''}
                        </Typography>
                    </CardActions>
                </Card>
            </Grid>
            <br />
            <br />
            <br />
            <Grid container spacing={3}>
                {projects.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((project) => (

                    <Grid xs={4} className={classes.row} key={project.id}>
                        <Card sx={{ maxWidth: 345 }}>
                            <CardMedia
                                component="img"
                                alt="green iguana"
                                height="140"
                                image={project.image ? project.image : defaultImg}
                            />
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="div">
                                    {project.name}
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    {project.description}
                                </Typography>
                            </CardContent>

                            <CardActions>
                                <Button className={classes.button} variant="contained" style={{ marginRight: 10 }} component={Link} to={`/program/services/${program._id}`}><InfoIcon />Ver más</Button>
                            </CardActions>
                        </Card>
                    </Grid>
                ))}

            </Grid>


        </div>
    );
}
